function updateSkillsPanelHeight(){var e=document.getElementsByClassName("main-short-description")[0].clientHeight,t=document.getElementsByClassName("info-subpanel")[0].clientHeight,n=document.body.clientHeight-(e+t+64+document.querySelector(".skills-subpanel > .left-panel-heading").clientHeight);document.getElementsByClassName("skills-body")[0].style.maxHeight=n+"px"}function updateRightPanelWidth(){var e=document.getElementsByClassName("left-panel")[0].clientWidth;document.getElementsByClassName("right-panel")[0].style.marginLeft="calc("+e+"px + 1em)"}document.addEventListener("DOMContentLoaded",e=>{updateSkillsPanelHeight(),updateRightPanelWidth(),window.onresize=(e=>{this.updateSkillsPanelHeight(),this.updateRightPanelWidth()})});